if ((!bOnlyIfTargetIsNull) || (this.getField(BookingHotel.NIGHTS).isNull()))
    this.getField(BookingHotel.NIGHTS).setValue(iNights, DBConstants.DISPLAY, DBConstants.INIT_MOVE);
