if ((index >=0) && (index <= 2))
	tempString = string.substring(index, index + 1);
else
	tempString = "A";
return tempString;

String string = "AMU";
for (int index = 0; index <= 1; index++)
{
	if (tempString.equalsIgnoreCase(string.substring(index, index+1)))
		return index;
}
return -1;

