this.getMainRecord().getField(ApTrx.INVOICE_DATE).addListener(new InitFieldHandler(this.getScreenRecord().getField(InvoiceScreenRecord.LAST_INVOICE_DATE)));
// If vendor change, set new last vendor, clear all last.
