int iErrorCode = super;
if (iErrorCode != DBConstants.NORMAL_RETURN)
	return iErrorCode;
if (iChangeType == DBConstants.SELECT_TYPE)
	m_recBookingPax.movePaPaxDetail(m_recProfile);
