    strCommand = this.addScreenParams(this, strCommand);
    this.getParentScreen().popHistory(1, false);
    this.getParentScreen().pushHistory(strCommand, false);

    strCommand = Utility.addURLParam(null, DBParams.SCREEN, strScreen);
    strCommand = this.addScreenParams(this, strCommand);
