if (iErrorCode == DBConstants.NORMAL_RETURN)
    if (bState == true) if (this.getState() == false)
        ((BaseField)this.getField()).moveFieldToThis(((BaseField)this.getField()).getRecord().getField(ApTrx.INVOICE_BALANCE));
return iErrorCode;

