                m_recTrxType.getField(TransactionType.SOURCE_FILE).setData(null);
                m_recTrxType.getField(TransactionType.SOURCE_TRX_DESC_ID).setData(null);
            }
            m_recTrxType.set();
        }
    } catch (DBException ex) {
        ex.printStackTrace();
    }
}
