String startDate = ((DateTimeField)this.getMainRecord().getField(BookingDetail.DETAIL_DATE)).toString();
if (startDate == null)
    startDate = ((DateTimeField)this.getRecord(Tour.TOUR_FILE).getField(Tour.DEPARTURE_DATE)).toString();
