    for (int index = 1; ;index++)
    {
        productMessageDesc.setNodeIndex(index, null);
        if ((productMessageDesc.get(BookingDetail.PRODUCT_ID) == null) && (productMessageDesc.get(Product.CODE) == null))
            break;  // End of product descriptions
