class RefreshItin implements Runnable
{
    BookingItinerary m_screen = null;
    Date m_timeRequested = null;
    public RefreshItin(BookingItinerary screen)
    {
        m_screen = screen;
        m_timeRequested = new Date();
    }
    public void run()
    {
        m_screen.refreshText(m_timeRequested);
    }
}
